import React, { Component } from "react";
import "./style.css";
export default class OrderItem extends Component {
  render() {
    const { product, shop, price, picture, ifCommented } = this.props.value;
    return (
      <div className="orderItem">
        <div className="orderItem__picContainer">
          <img className="orderItem__pic" src={picture}></img>
        </div>
        <div className="orderItem__content">
          <div className="orderItem__product">{product}</div>
          <div className="orderItem__shop">{shop}</div>
          <div className="orderItem__detail">
            <div className="orderItem__price">{price}</div>
            <div>
              {ifCommented ? (
                <button className="orderItem__btn orderItem_btm--grey">
                  已评价
                </button>
              ) : (
                <button className="orderItem__btn orderItem_btm--red">
                  评价
                </button>
              )}
            </div>
          </div>
        </div>
      </div>
    );
  }
  renderEditArea(){
      return(
          <div>
              <textarea></textarea>
              <button className="orderItem__btn orderItem_btm--red">评价</button>
              <button className="orderItem__btn orderItem_btm--grey">取消</button>
          </div>
      )
  }
}
